const sendMessage = async () => {
  const prompt = 'Hello, ChatGPT!';

  try {
    const response = await axios.post('https://api.openai.com/v1/engines/davinci-codex/completions', {
      prompt: prompt,
      max_tokens: 50, // Adjust the number of tokens based on your requirements
      temperature: 0.7, // Adjust the temperature for generating diverse responses
    }, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer YOUR_API_KEY`,
      },
    });

    const reply = response.data.choices[0].text.trim();
    console.log(reply); // Handle the generated reply as desired
  } catch (error) {
    console.error(error);
  }
}
